<?php/** * The Sidebar containing the main widget areas. * * @package dazzling */?>	<div id="secondary" class="widget-area col-sm-12 col-md-4" role="complementary">		<?php do_action( 'before_sidebar' ); ?>		<?php if ( ! dynamic_sidebar( 'sidebar-1' ) ) : ?>                <div class="widget tabbed">  <div class="tabs-wrapper">    <ul class="nav nav-tabs">      <li class="active"><a href="#popular-posts" data-toggle="tab">        <?php esc_attr_e( 'Popular', 'dazzling' ); ?>        </a></li>      <li><a href="#recent" data-toggle="tab">        <?php esc_attr_e( 'Recent', 'dazzling' ); ?>        </a></li>      <li><a href="#messages" data-toggle="tab"><i class="fa fa-comments tab-comment"></i></a></li>    </ul>    <div class="tab-content">      <ul id="popular-posts" class="tab-pane active">        <?php                        $recent_posts = new WP_Query( array(                            'showposts'           => 5,                            'ignore_sticky_posts' => 1,                            'post_status'         => 'publish',                            'order'               => 'DESC',                            'meta_key'            => 'post_views_count',                            'orderby'             => 'meta_value_num'                        ) );                    ?>        <?php while($recent_posts->have_posts()): $recent_posts->the_post(); ?>        <li>          <?php if ( has_post_thumbnail() ) : ?>          <a href="<?php echo get_permalink() ?>" class="tab-thumb thumbnail" rel="bookmark" title="<?php the_title(); ?>">          <?php the_post_thumbnail('tab-small'); ?>          </a>          <?php endif; ?>          <div class="content"> <a class="tab-entry" href="<?php echo get_permalink() ?>" rel="bookmark" title="<?php the_title(); ?>">            <?php the_title(); ?>            </a> <i>            <?php the_time('M j, Y') ?>            </i> </div>        </li>        <?php endwhile; ?>      </ul>      <?php wp_reset_postdata(); ?>      <ul id="recent" class="tab-pane">        <?php                        $recent_posts = new WP_Query( array(                            'showposts'           => 5,                            'post_status'         => 'publish',                            'ignore_sticky_posts' => 1                        )) ;                    ?>        <?php while($recent_posts->have_posts()): $recent_posts->the_post(); ?>        <li>          <?php if ( has_post_thumbnail() ) : ?>          <a href="<?php echo get_permalink(); ?>" class="tab-thumb thumbnail" rel="bookmark" title="<?php the_title(); ?>">          <?php the_post_thumbnail( 'tab-small' ); ?>          </a>          <?php endif; ?>          <div class="content"> <a class="tab-entry" href="<?php echo get_permalink() ?>" rel="bookmark" title="<?php the_title(); ?>">            <?php the_title(); ?>            </a> <i>            <?php the_time( 'M j, Y' ) ?>            </i> </div>        </li>        <?php endwhile; ?>      </ul>      <?php wp_reset_postdata(); ?>      <ul id="messages" class="tab-pane">        <?php                    $recent_comments = get_comments( array(                        'number'    => 5,                        'status'    => 'approve'                    ) );                ?>        <?php foreach($recent_comments as $comment) : ?>        <li>          <div class="content">            <?php if ( $comment->comment_author ) { echo $comment->comment_author; } else { _e('Anonymous','dazzling'); } ?>            <?php _e('on','dazzling'); ?>            <a href="<?php echo get_permalink($comment->comment_post_ID) ?>" rel="bookmark" title="<?php echo get_the_title($comment->comment_post_ID); ?>"> <?php echo get_the_title($comment->comment_post_ID); ?> </a>            <p> <?php echo substr( $comment->comment_content, 0, strrpos( substr( $comment->comment_content, 0, 60 ), ' ' ) ); ?>              <?php if ( strlen( $comment->comment_content ) > 60 ) { echo '(...)'; } ?>            </p>          </div>        </li>        <?php endforeach; ?>      </ul>    </div>  </div></div>                                        			<aside id="search" class="widget widget_search">				<?php get_search_form(); ?>			</aside>			<aside id="archives" class="widget">				<h1 class="widget-title"><?php _e( 'Archives', 'dazzling' ); ?></h1>				<ul>					<?php wp_get_archives( array( 'type' => 'monthly' ) ); ?>				</ul>			</aside>                   			<aside id="meta" class="widget">				<h1 class="widget-title"><?php _e( 'Meta', 'dazzling' ); ?></h1>				<ul>					<?php wp_register(); ?>					<li><?php wp_loginout(); ?></li>					<?php wp_meta(); ?>				</ul>			</aside>		<?php endif; // end sidebar widget area ?>	</div><!-- #secondary -->