<?phpfunction gskh_topics_init() {	register_post_type( 'gskh_topics', array(		'labels'            => array(			'name'                => __( 'Gskh topics', 'dazzling' ),			'singular_name'       => __( 'Gskh topics', 'dazzling' ),			'all_items'           => __( 'All Gskh topics', 'dazzling' ),			'new_item'            => __( 'New gskh topics', 'dazzling' ),			'add_new'             => __( 'Add New', 'dazzling' ),			'add_new_item'        => __( 'Add New gskh topics', 'dazzling' ),			'edit_item'           => __( 'Edit gskh topics', 'dazzling' ),			'view_item'           => __( 'View gskh topics', 'dazzling' ),			'search_items'        => __( 'Search gskh topics', 'dazzling' ),			'not_found'           => __( 'No gskh topics found', 'dazzling' ),			'not_found_in_trash'  => __( 'No gskh topics found in trash', 'dazzling' ),			'parent_item_colon'   => __( 'Parent gskh topics', 'dazzling' ),			'menu_name'           => __( 'Gskh topics', 'dazzling' ),		),		'public'            => true,		'hierarchical'      => false,		'show_ui'           => true,		'show_in_nav_menus' => true,		'supports'          => array( 'title', 'editor' ),		'has_archive'       => true,		'rewrite'           => true,		'query_var'         => true,		'menu_icon'         => 'dashicons-admin-post',		'show_in_rest'      => true,		'rest_base'         => 'gskh_topics',		'rest_controller_class' => 'WP_REST_Posts_Controller',		'taxonomies' => array('category'),	) );}add_action( 'init', 'gskh_topics_init' );function gskh_topics_updated_messages( $messages ) {	global $post;	$permalink = get_permalink( $post );	$messages['gskh_topics'] = array(		0 => '', // Unused. Messages start at index 1.		1 => sprintf( __('Gskh topics updated. <a target="_blank" href="%s">View gskh topics</a>', 'dazzling'), esc_url( $permalink ) ),		2 => __('Custom field updated.', 'dazzling'),		3 => __('Custom field deleted.', 'dazzling'),		4 => __('Gskh topics updated.', 'dazzling'),		/* translators: %s: date and time of the revision */		5 => isset($_GET['revision']) ? sprintf( __('Gskh topics restored to revision from %s', 'dazzling'), wp_post_revision_title( (int) $_GET['revision'], false ) ) : false,		6 => sprintf( __('Gskh topics published. <a href="%s">View gskh topics</a>', 'dazzling'), esc_url( $permalink ) ),		7 => __('Gskh topics saved.', 'dazzling'),		8 => sprintf( __('Gskh topics submitted. <a target="_blank" href="%s">Preview gskh topics</a>', 'dazzling'), esc_url( add_query_arg( 'preview', 'true', $permalink ) ) ),		9 => sprintf( __('Gskh topics scheduled for: <strong>%1$s</strong>. <a target="_blank" href="%2$s">Preview gskh topics</a>', 'dazzling'),		// translators: Publish box date format, see http://php.net/date		date_i18n( __( 'M j, Y @ G:i' ), strtotime( $post->post_date ) ), esc_url( $permalink ) ),		10 => sprintf( __('Gskh topics draft updated. <a target="_blank" href="%s">Preview gskh topics</a>', 'dazzling'), esc_url( add_query_arg( 'preview', 'true', $permalink ) ) ),	);	return $messages;}add_filter( 'post_updated_messages', 'gskh_topics_updated_messages' );